def mi():  # 这里可以获取请求过来的是数据
    def judge_phone(tel):
        tel = str(tel)
        ret = re.match(r"^1[35789]\d{9}$", tel)
        if ret is None:
            return "你输入的账号有误，请输入小米运动的账号（手机号）！"

    def no_empty_str(num):
        if len(num) == 0:
            return "请不要输入空数据！"

    def no_empty_num(num):
        if num < 0:
            return "输入负数你想干嘛呢你！？"
        elif num > 90000:
            return "刷这么多我怕你会封号！！！"

